const crypto = require('crypto');

const key = $vars.encrypt_key;
const iv = crypto.randomBytes(16);
const cipher = crypto.createCipheriv('aes-256-cbc', key, iv);

let encrypted = cipher.update($('Obtain Credentials').first().json.data.password, 'utf8', 'hex');
encrypted += cipher.final('hex');

return {
  email: $('Obtain Credentials').first().json.data.email,
  password: encrypted,
  iv: iv.toString('hex')
};

const crypto = require('crypto');

const key = $vars.encrypt_key;
const iv = Buffer.from($input.first().json.password_iv, 'hex');
const decipher = crypto.createDecipheriv('aes-256-cbc', key, iv);

let decrypted = decipher.update($input.first().json.password, 'hex', 'utf8');
decrypted += decipher.final('utf8');

return { email: $input.first().json.email, password: decrypted };

